const activities = data.map(a => {
    return(
        <Col lg={4}>
            <div className="activity">
                <img src={a.image} alt="place" />
                <p className="title">{a.title}</p>
                <p className="company">By {a.company}</p>
                <p className="my-cat">{a.category}</p>
            </div>
        </Col>
    )
})

const TopPicks = () => {





    var settings = {
        dots: false,
        lazyLoad: 'progressive',
        infinite: true,
        speed: 500,
        autoplay: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        responsive: [
			{
				breakpoint: 480,
				settings: {
					slidesToShow: 2,
					slidesToScroll: 1
				}
			}
		]
    };

    return (
        <section className="top-picks pick-box1">
            <Container>
                <Row>
                    <Col lg={6}>
                        <h3>Top Picks</h3>
                    </Col>
                    <Col lg={6}></Col>
                </Row>
                <Row className="bottom-line">
                    <Col lg={12}>
                        <Slider {...settings}>
                            {picks}
                        </Slider>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default TopPicks;
