const TopPicks = () => {

    const [topPicks, setTopPicks] = useState({})

    useEffect(() => {

        fetch(`https://qoolqatar.com/qool_qatar/api/homedata`)
            .then((response) => response.json())
            .then((data) => setTopPicks(data.luxury_picks));
    }, [])

    var settings = {
        dots: false,
        lazyLoad: 'progressive',
        infinite: true,
        speed: 500,
        autoplay: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        responsive: [
